mapper.update:
update ke_content_character
set content_id = #{contentId,jdbcType=INTEGER},
  character_id = #{characterId,jdbcType=INTEGER},
  order_id = #{orderId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, content_id, character_id, order_id
from ke_content_character
where id = #{id,jdbcType=INTEGER}
